/* Function  structure for function  `fnGetAbstract` */

/*!50003 DROP FUNCTION IF EXISTS `fnGetAbstract` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fnGetAbstract`(
    ibids_details_id int(11)) RETURNS decimal(10,2)
    READS SQL DATA
BEGIN
	return(SELECT ifnull(`tbl_bids_details`.`bids_price`,0) as `bids_price` FROM `tbl_bids_details`
	INNER JOIN `tbl_purchase_request_details` ON 
	`tbl_purchase_request_details`.`purchase_request_id` = `tbl_bids_details`.`purchase_request_id` 
	WHERE `tbl_bids_details`.`bids_details_id` = ibids_details_id
	GROUP BY `tbl_bids_details`.`bids_price`);
    END */$$
DELIMITER ;

/* Function  structure for function  `fnGetBids` */

