/*!50003 CREATE DEFINER=`root`@`localhost` FUNCTION `fnGetBids`(
    ibids_details_id int(11)) RETURNS decimal(10,2)
    READS SQL DATA
BEGIN
	return(select `tbl_bids_details`.`bids_price` from `tbl_bids_details` WHERE `tbl_bids_details`.`bids_details_id` = ibids_details_id);
