	IFNULL(`tbl_purchase_request_details`.`purchase_request_details_unit`,'') AS `purchase_request_details_unit`,IFNULL(`fnGetAbstract`(`tbl_bids_details`.`bids_details_id`),'0.00') AS PPrice
	FROM `tbl_purchase_request_details`
	INNER JOIN `tbl_bids_details`
	ON `tbl_bids_details`.`purchase_request_id` = `tbl_purchase_request_details`.`purchase_request_id`
	INNER JOIN `tbl_bids` ON `tbl_bids`.`bids_id` = `tbl_bids_details`.`bids_id`
	INNER JOIN `tbl_supplier` ON `tbl_supplier`.`supplier_id` = `tbl_bids`.`supplier_id`
	WHERE `tbl_bids_details`.`purchase_request_id`=iprid
	GROUP BY `tbl_bids_details`.`bids_details_id`
	ORDER BY `tbl_bids_details`.`bids_item_description`;	
	 
	DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
	DROP TABLE IF EXISTS `tmpheader`;
	-- Create Temporary Table
	SET @SQL=CONCAT('CREATE TABLE `tmpheader`(
	tempid INT(11) NOT NULL AUTO_INCREMENT,
