	   END LOOP;
	 CLOSE my_cursor;
	 SET @SQL = CONCAT(@SQL,'PRIMARY KEY (`tempid`)) ;');
	     PREPARE stmt1 FROM @SQL;
	     EXECUTE stmt1; 
	     DEALLOCATE PREPARE stmt1;
		SET @INSERTS = CONCAT('INSERT INTO `tmpheader`(`headerid`,`itemno`,`qty`,`unit`,`item_description`,');
			   OPEN my_cursor;
			     SELECT FOUND_ROWS() INTO len;
			     SET done=0;
			     SET s = 0;
			     read_loop : LOOP
			        SET s = s + 1;
				FETCH my_cursor INTO supplier_id,supplier_name;
				IF done THEN
				   LEAVE read_loop;
				END IF;
				SET @c = ",";
				IF len <= s THEN
				SET @c = ")";
				END IF;
			   END LOOP;
			 CLOSE my_cursor;	 
	SET done = 0;
	SET s = 0;		
	SET @bb='';
	OPEN my_cursor3;
	    sloop: LOOP
	   	FETCH my_cursor3 INTO remarks,purchase_request_details_id,supplier_id,supplier_name,bids_details_id,bids_count,item_description,purchase_request_quantity,purchase_request_details_unit,PPrice;	    
		IF done THEN
	          LEAVE sloop;
	        END IF;
	
	    IF oldPS IS NULL OR oldPS != purchase_request_details_id THEN
		SET s = s + 1;
		SET oldPS = purchase_request_details_id;
		SET @bb = CONCAT(@INSERTS,"`",supplier_name,"`" ,@c," VALUES('",bids_details_id,"','",s,"','",purchase_request_quantity,"','",purchase_request_details_unit,"','",item_description,"','",PPrice,"<br/>",remarks,"'",");");
		ELSE
		SELECT LAST_INSERT_ID() INTO mddi;
		SET @bb = CONCAT("UPDATE `tmpheader` SET ","`",supplier_name,"`='",PPrice,"<br/>",remarks,"' WHERE `tempid`=",mddi);
	    END IF;
		     PREPARE stmt2 FROM @bb;
		     EXECUTE stmt2; 
		     DEALLOCATE PREPARE stmt2;	 
	  END LOOP;
	  CLOSE my_cursor3;
	 SELECT * FROM `tmpheader`;
    END */$$
