/* Procedure structure for procedure `spGenerateListPO` */

/*!50003 DROP PROCEDURE IF EXISTS  `spGenerateListPO` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`fais`@`%` PROCEDURE `spGenerateListPO`(
    iPurchaseRequestID int(11),
    iCount int(11))
    READS SQL DATA
BEGIN
    if iCount = 1 then
        SELECT count(*) FROM `fais-procurement`.`tbl_purchase_order`
	INNER JOIN `fais-procurement`.`tbl_purchase_order_details` ON `tbl_purchase_order_details`.`purchase_order_id` = `tbl_purchase_order`.`purchase_order_id` 
	INNER JOIN `fais-procurement`.`tbl_bids_details` ON `tbl_bids_details`.`bids_details_id` = `tbl_purchase_order_details`.`bids_details_id`
	INNER JOIN `fais-procurement`.`tbl_bids` ON `tbl_bids`.`bids_id` = `tbl_bids_details`.`bids_id`
	WHERE `tbl_bids`.`purchase_request_id` = iPurchaseRequestID ORDER BY `tbl_purchase_order`.`purchase_order_number` limit 10;
    else
    SELECT `tbl_bids`.`bids_id`,`tbl_bids_details`.`purchase_request_id`,
	`tbl_bids`.`supplier_id`,`fnGetSupplierName`(`tbl_bids`.`supplier_id`) AS SupplierName,`tbl_purchase_order`.`purchase_order_number` , 
	`tbl_bids_details`.`bids_unit` , `tbl_bids_details`.`bids_item_description` , 
	`tbl_bids_details`.`bids_quantity`,`tbl_bids_details`.`bids_price` FROM `fais-procurement`.`tbl_purchase_order`
	INNER JOIN `fais-procurement`.`tbl_purchase_order_details` ON `tbl_purchase_order_details`.`purchase_order_id` = `tbl_purchase_order`.`purchase_order_id` 
	INNER JOIN `fais-procurement`.`tbl_bids_details` ON `tbl_bids_details`.`bids_details_id` = `tbl_purchase_order_details`.`bids_details_id`
	INNER JOIN `fais-procurement`.`tbl_bids` ON `tbl_bids`.`bids_id` = `tbl_bids_details`.`bids_id`
	WHERE `tbl_bids`.`purchase_request_id` = iPurchaseRequestID ORDER BY `tbl_purchase_order`.`purchase_order_number` limit 10;
    END IF;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `spGenerateSupplier` */

/*!50003 DROP PROCEDURE IF EXISTS  `spGenerateSupplier` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `spGenerateSupplier`(
	iPurchaseRequestID int(11),
	iSupName varchar(100))
    READS SQL DATA
BEGIN
	SELECT `tbl_supplier`.`supplier_id` as id , `tbl_supplier`.`supplier_name` as `text` FROM `fais-procurement`.`tbl_supplier` WHERE `tbl_supplier`.`supplier_name` like concat('%', iSupName ,'%') 
	AND `tbl_supplier`.`supplier_id` NOT IN 
	(SELECT `tbl_bids`.`supplier_id` FROM `fais-procurement`.`tbl_bids` WHERE `tbl_bids`.`purchase_request_id`=iPurchaseRequestID);
	
    END */$$
